#!/usr/bin/env bash

USER=$(whoami)
export DEBIAN_FRONTEND=noninteractive
export PATH="$HOME/.local/bin:$PATH"
rm -rf /var/lib/dpkg/lock
rm -rf /var/cache/debconf/*.*
rm -rf /var/lib/apt/lists/lock
rm -rf /var/cache/apt/archives/lock
dpkg --configure -a

# colors
NORMAL=`tput sgr0`
RED=`tput setaf 1`
GREEN=`tput setaf 2`
Done="${GREEN}Done âœ“${NORMAL}"

apt update -y

apt upgrade -y

echo "${RED}Checking for updates.${NORMAL}"
sleep 1
 apt-get -y update
 apt-get -y upgrade
echo $Done

echo "${RED}Adding a auto updater to crontab${NORMAL}"
sleep 1
 crontab -l > updater
 echo "0 0 * * *     apt-get update &&  apt-get upgrade -y &&  apt autoremove && echo updated@SUCCESS >> ~/.update.log" >> updater
 crontab updater
 rm updater
echo $Done

